import { StatusBar } from "expo-status-bar";
import React from "react";
import {
  StyleSheet,
  Text,
  View,
  Alert,
  FlatList,
  ImageBackground,
  Image,
  Dimensions,
  TouchableHighlight,
  AsyncStorage,
  ScrollView
} from "react-native";

import SessionNavbar from "./security/SessionNavbar";
import Home from "../screens/Main";
const bgImg = require("../../assets/bg/y.jpg");

export default class Main extends React.Component {
    constructor(props) {
        super(props);
      }
    
      state = {
        isLoggedIn: false
      };
    
      async componentDidMount() {
        let session = await AsyncStorage.getItem("session");
    
        if (session) {
          
          console.log(session)
          this.setState({
            
            isLoggedIn: true,
          });
        }
      }
  
  render() {
    const { navigation } = this.props;
    if(this.state.isLoggedIn === false){
        return(
            <ImageBackground source={bgImg} style={styles.backgroundApp}>    
            <View style={styles.homeView}>  
            <Text style={{ color: "#760893",   fontWeight: 'bold', fontSize: 27}}>Bienvenido a Matching</Text>
            <ScrollView  style={{backgroundColor:"white", marginStart:10, marginEnd:10, marginVertical:30}}>
            <Text style={styles.texto}>¿Por qué Matchingggggggg?</Text>
            <Text style={styles.texto}>¿Cómo funciona Matching?</Text>
            <Text style={styles.texto}>Testimonios</Text>
            
            
            </ScrollView>
            </View>
            </ImageBackground>
          );
    }else{
        return <Home navigation={navigation}></Home>
    }
   
}
}

const styles = StyleSheet.create({
  imagenHome: {
    height:500,
    width: "100%",
    marginTop:30,
    paddingEnd:30
  },
  backgroundApp: {
    flex: 1,
     width: Dimensions.get("window").width ,
     height:500,
    
  },

  texto:{
    alignItems: "center",
    justifyContent: "center",

    color: "black", 
    fontWeight: 'bold',
     fontSize: 27
  },
  homeView: {
    alignItems: "center",
    alignContent: "center",
  },
});
