import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  Alert,
  TouchableOpacity,
  TextInput,
  ImageBackground,
  SafeAreaView,
  Button,
  AsyncStorage,
  Linking,
  Picker,
  CheckBox,
  Dimensions,
  View,
} from "react-native";
import { ScrollView } from "react-native-gesture-handler";

const bgImg = require("../../../assets/bg/r.jpg");

export default class Register extends React.Component {
    constructor(props) {
      super(props);
    }
    state = {
      nombre:"",
      correo: "",
      phone: "",
      sexo: "",
      fechaNacimiento:"",
      nivelEscolaridad :"",
      estadoCivil: "",
      orientacionSexual: "",
      ocupacion:"",
      hijos:false,
      fumador:false,
      bebedor:false,
      intereses:"",
      paisId:"",
      ciudadId:"",
      url: "http://192.168.0.2:3000/perfil",
    };
    
    async onRegister() {
      const n = this.state.nombre;
      const u = this.state.correo;
      const p = this.state.phone;
      const s = this.state.sexo;
      const f = this.state.fechaNacimiento;
      const ne = this.state.nivelEscolaridad;
      const ec = this.state.estadoCivil;
      const os = this.state.orientacionSexual;
      const o = this.state.ocupacion;
      const h = this.state.hijos;
      const fm = this.state.fumador;
      const i= this.state.intereses;
      const pI = this.state.paisId;
      const cI= this.state.ciudadId;

      fetch(this.state.url, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nombre: n,
          correo: u,
          phone: p,
          sexo: s,
          fechaNacimiento: f,
          nivelEscolaridad: ne,
          estadoCivil: ec,
          orientacionSexual: os,
          ocupacion:o,
          hijos: h,
          fumador: fm,
          intereses: i,
          paisId: pI,
          ciudadId: cI
        }),
      })
        .then((data) => data.json())
        .then((data) => {
          if (data.error) {
            Alert.alert("Advertencia", "Datos inválidos.");
          } else {
            this.state.nombre = "";
            this.state.correo = "";
            this.state.phone = "";
            this.state.sexo = "";
            this.state.fechaNacimiento = "";
            this.state.nivelEscolaridad = "";
            this.state.estadoCivil = "";
            this.state.orientacionSexual = "";
