import { StatusBar } from "expo-status-bar";
import React from "react";
import {
  StyleSheet,
  Text,
  Alert,
  TouchableOpacity,
  TextInput,
  ImageBackground,
  SafeAreaView,
  Button,
  AsyncStorage,
  Linking,
  Picker,
} from "react-native";

const bgImg = require("../../../assets/bg/mobile_bg_2.jpg");
export default class Register extends React.Component {
    constructor(props) {
      super(props);
    }
    state = {
      nombre:"",
      correo: "",
      phone: "",
      password: "",
      fechaNacimiento:"",
      nivelEscolaridad :"",
      orientacionSexual: "",
      ocupacion:"",
      hijos:"",
      fumador:"",
      bebedor:"",
      intereses:"",
      paisId:"",
      ciudadId:"",
      url: "http://192.168.0.2:3000/perfil",
    };
    
    async onRegister() {
      const n = this.state.nombre;
      const u = this.state.correo;
      const p = this.state.phone;
      const f = this.state.fechaNacimiento;
      const ne = this.state.nivelEscolaridad;
      const os = this.state.orientacionSexual;
      const o = this.state.ocupacion;
      const h = this.state.hijos;
      const fm = this.state.fumador;
      const i= this.state.intereses;

      fetch(this.state.url, {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          nombre: n,
          correo: u,
          phone: p,
          fechaNacimiento: f,
          nivelEscolaridad: ne,
          orientacionSexual: os,
          ocupacion:o,
          hijos: h,
          fumador: fm,
          intereses: i
        }),
      })
        .then((data) => data.json())
        .then((data) => {
          if (data.error) {
            Alert.alert("Advertencia", "Datos inválidos.");
          } else {
            this.state.correo = "";
            this.state.password = "";
            AsyncStorage.setItem("session", JSON.stringify(data));
            this.props.navigation.push("Iniciar sesión");
          }
        })
  
        .catch((err) => {
          Alert.alert("Advertencia", "Datos inválidos.");
        });
    }
  
    render() {
      const { navigation } = this.props;
      return (
        <ImageBackground source={bgImg} style={styles.backgroundApp}>
          <SafeAreaView style={styles.container}>
            <Text style={styles.titleText}>Registrarse</Text>
            <Text style={{ color:"white",fontSize: 18 }}>Por favor, introduce los datos requeridos correctamente.{"\n"}</Text>
        
            <TextInput
              value={this.state.correo}
              keyboardType="default"
              onChangeText={(correo) => this.setState({ correo })}
              placeholder="Nombre"
              placeholderTextColor="gray"
              style={styles.input}
            />
            <TextInput
              value={this.state.correo}
              keyboardType="default"
              onChangeText={(correo) => this.setState({ correo })}
              placeholder="Correo electrónico"
              placeholderTextColor="gray"
              style={styles.input}
            />
            <TextInput
              value={this.state.correo}
              keyboardType="default"
              onChangeText={(correo) => this.setState({ correo })}
              placeholder="Teléfono"
              placeholderTextColor="gray"
              style={styles.input}
            />
            <Picker style={styles.pickerStyle}  
                        selectedValue={this.state.correo}  
                        onValueChange={(itemValue, itemPosition) =>  
                            this.setState({language: itemValue, choosenIndex: itemPosition})}  
                    >  
                    <Picker.Item label="Masculino" value="Masculino" />  
                    <Picker.Item label="Femenino" value="Femenino" />  
            </Picker>  
            
            <Text>{"\n"}</Text>
            <TouchableOpacity
              style={styles.button}
              onPress={this.onRegister.bind(this)}
            >
              <Text style={styles.buttonText}> Ingresar </Text>
            </TouchableOpacity>
            <Text style ={{color:"#FFFCB8",fontSize: 17,  borderColor: "black"}}onPress={() => this.props.navigation.push("Registrate")}> ¡Registrate ahora! </Text>
            <Text style={{color:"#FFFCB8", fontSize:17,textAlign:"right",borderColor: "black"}}onPress={() => this.props.navigation.push("Iniciar sesión")}> ¿Olvidaste tu contraseña? </Text>
          </SafeAreaView>
        </ImageBackground>
      );
    }
  }
  
  const styles = StyleSheet.create({
    backgroundApp: {
      flex: 1,
      width: "100%",
    },
    container: {
      flex: 10,
      alignItems: "center",
      justifyContent: "center",
    },
    titleText: {
      fontSize: 60,
      alignItems: "center",
      justifyContent: "center",
      color: "#FFFCB8",
      fontWeight: 'bold',
    },
    button: {
      alignItems: "center",
      backgroundColor: "#FFFCB8",
      width: 300,
      height: 45,
      padding: 10,
      borderRadius: 20,
      marginBottom: 10,
      borderWidth: 1,
      borderColor: "#FDF994",
    },
    buttonText: {
      fontSize: 20,
      alignItems: "center",
      justifyContent: "center",
      color:"#4A0685"
    },
    input: {
      width: 400,
      fontSize: 15,
      height: 44,
      padding: 10,
      borderWidth: 1,
      borderColor: "white",
      backgroundColor: "#fff",
      marginVertical: 10,
      borderRadius: 10,
    },
    pickerStyle:{  
        height: 45,  
        width: 400,  
        color: 'gray',  
        justifyContent: 'center', 
        borderRadius: 10, 
    }  
  });
